#!/usr/bin/env python3from flask import Flask, request, make_response, Responsefrom plivo import plivoxmlapp = Flask(__name__)@app.route('/forward_call/', methods=['GET', 'POST'])def forwardcall():    response = plivoxml.ResponseElement()    response.add(        plivoxml.DialElement().add(            plivoxml.NumberElement('+1 844-547-0676')))    return(response.to_string())if __name__ == '__main__':    app.run(host='0.0.0.0', port=3000, debug=True)